import React, {Component} from 'react';
import Particles from 'react-particles-js';
import Navigation from './components/Navigation/navigation';
import Logo from './components/Logo/logo';
import ImageLinkForm from './components/ImageLinkForm/imageLinkForm';
import Clarifai from 'clarifai';
import Rank from './components/Rank/rank';
import FaceRecognition from './components/FaceRecognition/faceRecognition';
import SignIn from './components/SignIn/signIn';
import RegisterForm from './components/Register/register';
import './App.css';

const app = new Clarifai.App({
 apiKey: '104e27b470054aa187d3ce35278ba2e4'
});

const particleOptions = {
  particles: {
    number: {
      value: 80,
      density: {
        enable: true,
        value_area: 700
      }
    }
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
      isSignedIn: false
      } 
    }


  onInputChange = (event) => {
      this.setState({input: event.target.value});
  }  

  calculateFaceLocation = (data) => {
    const faceLocation = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('imageinput');
    const height = Number(image.height);
    const width = Number(image.width);
    return {
      leftCol: faceLocation.left_col * width,
      topRow: faceLocation.top_row * height,
      rightCol: width - (faceLocation.right_col * width),
      bottomRow: height - (faceLocation.bottom_row * height)
    }
  }

  faceBox = (box) => {
    this.setState({box: box});
  } 

  onSubmit = () => {
    this.setState({imageUrl: this.state.input});
    app.models
      .predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
      .then(response => this.faceBox(this.calculateFaceLocation(response)))
      .catch(err => console.log(err));
  }

  onRouteChange = (route) => {
    if (route === 'home') {
      this.setState({isSignedIn: true})
    } 
    else if (route === 'signin') {
      this.setState({isSignedIn: false})
    }
    this.setState({route: route});
  }


  render() {
    const {isSignedIn, box, imageUrl, route} = this.state;
    return (
      <div className="App">
         <Particles className='particle'
          params={particleOptions} />
        <Navigation isSignedIn={isSignedIn} signOut={this.onRouteChange} />
        { route === 'home' 
        ? <div>
          <Logo />
          <Rank />
          <ImageLinkForm 
            onInputChange={this.onInputChange} 
            onSubmit={this.onSubmit} />
          <FaceRecognition faceBox={box} imageUrl={imageUrl}/>
        </div>
        : ( route === 'signin' 
          ? <SignIn onRouteChange={this.onRouteChange} />
          : <Register onRouteChange={this.onRouteChange} />
          )
        }     
      </div>
    );
  }
}

export default App;
